package com.fymod.ftf.config;import com.google.gson.annotations.Expose;public class ResultBase {	public static final String RESULT_SUCC = "1";	public static final String RESULT_FAIL = "2";	@Expose	protected String result = "2";	@Expose	protected String message = "";	@Expose	protected Object obj;	public String getResult() {		return result;	}	public void setResult(String result) {		this.result = result;	}	public String getMessage() {		return message;	}	public void setMessage(String message) {		this.message = message;	}	public Object getObj() {		return obj;	}	public void setObj(Object obj) {		this.obj = obj;	}	public ResultBase error(ErrorCode errorCode) {	    ResultBase resultBase =  new ResultBase();	    resultBase.setResult(String.valueOf(errorCode.value));	    resultBase.setMessage(errorCode.message);	    return resultBase;	}}